import React from 'react';
import { Box, Typography } from '@mui/material';

const NameScroller = ({ selectedName }) => {
    return (
        <Box
            sx={{
                height: 200,
                overflow: 'hidden',
                borderRadius: 2,
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
                justifyContent: 'center',
                bgcolor: '#f7f7f7',
                boxShadow: 3,
                position: 'relative'
            }}
        >
            {[...Array(5)].map((_, i) => (
                <Typography
                    key={i}
                    variant="h6"
                    sx={{
                        opacity: i === 2 ? 1 : 0.5,
                        fontSize: i === 2 ? '1.5rem' : '1rem',
                        fontWeight: i === 2 ? 'bold' : 'normal',
                        my: 0.5
                    }}
                >
                    {selectedName || "---"}
                </Typography>
            ))}
        </Box>
    );
};

export default NameScroller;
